import './App.css';
import ConnectButton from './ConnectButton';
import LiveScores from './LiveScores';
import BettingPage from './BettingPage'; // Import the BettingPage component
import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  const [isWalletConnected, setIsWalletConnected] = useState(false);

  const handleWalletConnection = (connected) => {
    setIsWalletConnected(connected);
  };

  return (
    <Router>
      <div className="App">
        <ConnectButton onWalletConnect={handleWalletConnection} />
        <header className="App-header">
          <h1>NBA Live Betting</h1>
        </header>
        <Routes>
          <Route
            path="/"
            element={<LiveScores isWalletConnected={isWalletConnected} />}
          />
          <Route
            path="/bet/:gameId"
            element={<BettingPage />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
