import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ConnectButton from './ConnectButton';
import LiveScores from './LiveScores';
import BetPage from './BettingPage';
import './App.css';

function App() {
  const [isWalletConnected, setIsWalletConnected] = useState(false);

  const handleWalletConnection = (connected) => {
    setIsWalletConnected(connected);
  };

  return (
    <Router>
      <div className="App">
        <ConnectButton onWalletConnect={handleWalletConnection} />
        <header className="App-header">
          <h1>NBA Live Betting</h1>
        </header>
        <Routes>
          <Route path="/" element={<LiveScores isWalletConnected={isWalletConnected} />} />
          <Route path="/bet/:gameId" element={<BetPage contractAddress="0x2905F0A41A80510898aE76290cf1eCFCA41650CC" abi={YOUR_CONTRACT_ABI} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
