// BettingPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ethers } from 'ethers';

function BettingPage() {
  const { gameId } = useParams(); // Get gameId from URL params
  const [gameDetails, setGameDetails] = useState(null);

  useEffect(() => {
    // Fetch game details from your backend or use static data for now
    async function fetchGameDetails() {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/game/${gameId}`);
        const data = await response.json();
        setGameDetails(data);
      } catch (error) {
        console.error("Error fetching game details:", error);
      }
    }

    fetchGameDetails();
  }, [gameId]);

  // Function to handle betting
  const handlePlaceBet = async (team) => {
    // Assuming you have the contract ABI and address
    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = provider.getSigner();
    const contractAddress = '0x2905F0A41A80510898aE76290cf1eCFCA41650CC';
    const contractABI = [
        {
          "inputs": [],
          "stateMutability": "nonpayable",
          "type": "constructor"
        },
        {
          "anonymous": false,
          "inputs": [
            { "indexed": true, "internalType": "address", "name": "bettor", "type": "address" },
            { "indexed": true, "internalType": "uint256", "name": "gameId", "type": "uint256" },
            { "indexed": false, "internalType": "enum SportsBetting.BetOption", "name": "option", "type": "uint8" },
            { "indexed": false, "internalType": "uint256", "name": "amount", "type": "uint256" }
          ],
          "name": "BetPlaced",
          "type": "event"
        },
        {
          "inputs": [
            { "internalType": "uint256", "name": "_gameId", "type": "uint256" },
            { "internalType": "enum SportsBetting.BetOption", "name": "_option", "type": "uint8" }
          ],
          "name": "placeBet",
          "outputs": [],
          "stateMutability": "payable",
          "type": "function"
        },
        // ... Add other parts of the ABI as needed
      ];

    const contract = new ethers.Contract(contractAddress, contractABI, signer);

    try {
      const betOption = team === 'teamA' ? 0 : 1; // 0 for Team A, 1 for Team B
      const betAmount = ethers.parseUnits("0.01", "ether"); // Example bet amount

      const tx = await contract.placeBet(gameId, betOption, { value: betAmount });
      await tx.wait();
      alert(`Bet placed on ${team}!`);
    } catch (error) {
      console.error("Betting error:", error);
      alert("Error placing bet.");
    }
  };

  if (!gameDetails) return <p>Loading game details...</p>;

  return (
    <div>
      <h2>Betting on {gameDetails.home.name} vs {gameDetails.away.name}</h2>
      <button onClick={() => handlePlaceBet('teamA')}>Bet on {gameDetails.home.name}</button>
      <button onClick={() => handlePlaceBet('teamB')}>Bet on {gameDetails.away.name}</button>
    </div>
  );
}

export default BettingPage;
