import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

// Define the contract ABI
const YOUR_CONTRACT_ABI = [
  {
    "inputs": [],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "anonymous": false,
    "inputs": [
      { "indexed": true, "internalType": "address", "name": "bettor", "type": "address" },
      { "indexed": true, "internalType": "uint256", "name": "gameId", "type": "uint256" },
      { "indexed": false, "internalType": "enum SportsBetting.BetOption", "name": "option", "type": "uint8" },
      { "indexed": false, "internalType": "uint256", "name": "amount", "type": "uint256" }
    ],
    "name": "BetPlaced",
    "type": "event"
  },
  {
    "inputs": [
      { "internalType": "uint256", "name": "_gameId", "type": "uint256" },
      { "internalType": "enum SportsBetting.BetOption", "name": "_option", "type": "uint8" }
    ],
    "name": "placeBet",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  // ... Add other parts of the ABI as needed
];

// Define the contract address
const CONTRACT_ADDRESS = "YOUR_CONTRACT_ADDRESS";

function BettingPage({ gameId, teamA, teamB }) {
  const [betOption, setBetOption] = useState(null);
  const [betAmount, setBetAmount] = useState("");
  const [contract, setContract] = useState(null);
  const [signer, setSigner] = useState(null);

  useEffect(() => {
    const initContract = async () => {
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        await provider.send("eth_requestAccounts", []); // Request wallet connection
        const _signer = provider.getSigner();
        setSigner(_signer);

        // Initialize the contract
        const bettingContract = new ethers.Contract(CONTRACT_ADDRESS, YOUR_CONTRACT_ABI, _signer);
        setContract(bettingContract);
      } else {
        alert("Please install MetaMask to place bets.");
      }
    };

    initContract();
  }, []);

  const placeBet = async () => {
    if (!contract) {
      console.error("Contract not initialized");
      return;
    }

    if (!betOption || !betAmount) {
      alert("Please select a team and enter an amount.");
      return;
    }

    try {
      const transaction = await contract.placeBet(
        gameId,
        betOption, 
        { value: ethers.utils.parseEther(betAmount) } // Convert bet amount to Wei
      );
      await transaction.wait(); // Wait for the transaction to be mined
      alert("Bet placed successfully!");
    } catch (error) {
      console.error("Error placing bet:", error);
      alert("Failed to place bet.");
    }
  };

  return (
    <div className="betting-page">
      <h2>Bet on {teamA} vs {teamB}</h2>
      <div>
        <label>
          Select Team:
          <select onChange={(e) => setBetOption(e.target.value)}>
            <option value="">Select a team</option>
            <option value="0">Team A - {teamA}</option>
            <option value="1">Team B - {teamB}</option>
          </select>
        </label>
      </div>
      <div>
        <label>
          Bet Amount (in ETH):
          <input
            type="text"
            value={betAmount}
            onChange={(e) => setBetAmount(e.target.value)}
          />
        </label>
      </div>
      <button onClick={placeBet}>Place Bet</button>
    </div>
  );
}

export default BettingPage;
