// BettingPage.js
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { ethers } from 'ethers';
import SportsBettingABI from './SportsBettingABI.json';
import './BettingPage.css';

const contractAddress = ''; // Replace with actual deployed contract address

function BettingPage() {
  const { state } = useLocation();
  const game = state?.game;
  const [betAmount, setBetAmount] = useState('');
  const [betOption, setBetOption] = useState(''); // "TeamA" or "TeamB"
  const [transactionHash, setTransactionHash] = useState('');

  const handleBet = async () => {
    if (!betAmount || !betOption) {
      alert('Please select a team and enter an amount.');
      return;
    }

    if (window.ethereum) {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, SportsBettingABI.abi, signer);

      try {
        // Parse the bet amount and convert to the required format for the contract
        const value = ethers.parseEther(betAmount);

        // Place the bet
        const tx = await contract.placeBet(game.id, betOption === 'TeamA' ? 0 : 1, { value });
        await tx.wait(); // Wait for transaction to be mined

        setTransactionHash(tx.hash);
        alert('Bet placed successfully!');
      } catch (error) {
        console.error('Error placing bet:', error);
        alert('Bet failed. Please try again.');
      }
    } else {
      alert('MetaMask is not installed.');
    }
  };

  return (
    <div className="betting-page">
      <h2>Betting on: {game.home.name} vs {game.away.name}</h2>
      <div className="betting-input">
        <label>
          Bet Amount (ETH):
          <input
            type="text"
            value={betAmount}
            onChange={(e) => setBetAmount(e.target.value)}
          />
        </label>
      </div>
      <div className="betting-input">
        <label>
          Choose Team:
          <select value={betOption} onChange={(e) => setBetOption(e.target.value)}>
            <option value="">Select Team</option>
            <option value="TeamA">{game.home.name}</option>
            <option value="TeamB">{game.away.name}</option>
          </select>
        </label>
      </div>
      <button onClick={handleBet} className="place-bet-button">Place Bet</button>
      {transactionHash && (
        <p>Transaction Hash: <a href={`https://etherscan.io/tx/${transactionHash}`} target="_blank" rel="noopener noreferrer">{transactionHash}</a></p>
      )}
    </div>
  );
}

export default BettingPage;
