// BettingPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ethers } from 'ethers';
import SportsBettingABI from '../contracts/SportsBettingABI.json';

function BettingPage() {
  const { gameId } = useParams(); // Get gameId from URL params
  const [gameDetails, setGameDetails] = useState(null);
  const contractAddress = 'YOUR_CONTRACT_ADDRESS_HERE'; // Replace with your contract's deployed address

  useEffect(() => {
    // Fetch game details from your backend or use static data for now
    async function fetchGameDetails() {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/game/${gameId}`);
        const data = await response.json();
        setGameDetails(data);
      } catch (error) {
        console.error("Error fetching game details:", error);
      }
    }

    fetchGameDetails();
  }, [gameId]);

  // Function to handle placing a bet
  const handlePlaceBet = async (team) => {
    if (!window.ethereum) {
      alert("MetaMask is required to place a bet!");
      return;
    }

    try {
      // Initialize ethers and connect to MetaMask
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, SportsBettingABI.abi, signer);

      const betOption = team === 'teamA' ? 0 : 1; // 0 for Team A, 1 for Team B
      const betAmount = ethers.parseUnits("0.01", "ether"); // Example bet amount of 0.01 ETH

      // Call the placeBet function from the contract
      const tx = await contract.placeBet(gameId, betOption, { value: betAmount });
      await tx.wait();
      alert(`Bet placed on ${team}!`);
    } catch (error) {
      console.error("Betting error:", error);
      alert("Error placing bet.");
    }
  };

  if (!gameDetails) return <p>Loading game details...</p>;

  return (
    <div>
      <h2>Betting on {gameDetails.home.name} vs {gameDetails.away.name}</h2>
      <button onClick={() => handlePlaceBet('teamA')}>Bet on {gameDetails.home.name}</button>
      <button onClick={() => handlePlaceBet('teamB')}>Bet on {gameDetails.away.name}</button>
    </div>
  );
}

export default BettingPage;
