import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { BrowserProvider, Contract, parseEther } from 'ethers';

function BettingPage({ contractAddress, abi }) {
  const { gameId } = useParams();
  const [betAmount, setBetAmount] = useState("");

  const handleBet = async (option) => {
    if (!window.ethereum || !betAmount) {
      alert("Please enter a valid bet amount and connect to MetaMask.");
      return;
    }

    try {
      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new Contract(contractAddress, abi, signer);

      const tx = await contract.placeBet(gameId, option, { value: parseEther(betAmount) });
      await tx.wait();
      alert("Bet placed successfully!");
    } catch (error) {
      console.error("Error placing bet:", error);
      alert("Failed to place bet.");
    }
  };

  return (
    <div className="betting-page">
      <h2>Place Your Bet on Game {gameId}</h2>
      <input
        type="number"
        value={betAmount}
        onChange={(e) => setBetAmount(e.target.value)}
        placeholder="Enter bet amount in ETH"
        className="bet-amount-input"
      />
      <button className="bet-button bet-button-team-a" onClick={() => handleBet(0)}>
        Bet on Team A
      </button>
      <button className="bet-button bet-button-team-b" onClick={() => handleBet(1)}>
        Bet on Team B
      </button>
    </div>
  );
}

export default BettingPage;
