// WalletActions.js
import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import PaymentContractABI from './PaymentContract.json';
import './WalletActions.css';

const contractAddress = '0x12e31C3ECD08d3b5C0775F623db3bdd7ec1e2931';

function WalletActions() {
  const [amount, setAmount] = useState('');
  const [balance, setBalance] = useState('0');
  const [error, setError] = useState('');

  useEffect(() => {
    if (window.ethereum) {
      fetchBalance();
    }
  }, []);

  const fetchBalance = async () => {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, PaymentContractABI.abi, provider);
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();

      const userBalance = await contract.balances(userAddress);
      setBalance(ethers.formatEther(userBalance));
    } catch (error) {
      console.error('Error fetching balance:', error);
      setError('Failed to load balance');
    }
  };

  const handleDeposit = async () => {
    try {
      if (!amount) {
        alert('Please enter an amount to deposit.');
        return;
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, PaymentContractABI.abi, signer);

      const tx = await contract.deposit({ value: ethers.parseEther(amount) });
      await tx.wait();
      alert('Deposit successful!');
      setAmount('');
      fetchBalance();
    } catch (error) {
      console.error('Deposit error:', error);
      alert('Deposit failed. Please check the transaction details or try again.');
    }
  };

  const handleWithdraw = async () => {
    try {
      if (!amount) {
        alert('Please enter an amount to withdraw.');
        return;
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, PaymentContractABI.abi, signer);

      const tx = await contract.withdraw(ethers.parseEther(amount));
      await tx.wait();
      alert('Withdrawal successful!');
      setAmount('');
      fetchBalance();
    } catch (error) {
      console.error('Withdrawal error:', error);
      alert('Withdrawal failed. Please check the transaction details or try again.');
    }
  };

  return (
    <div className="wallet-actions">
      <p>Current Balance: {balance} ETH</p>
      <input
        type="text"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Enter amount in ETH"
      />
      <button onClick={handleDeposit}>Deposit</button>
      <button onClick={handleWithdraw}>Withdraw</button>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default WalletActions;
