import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ConnectButton from './ConnectButton';
import LiveScores from './LiveScores';
import BettingPage from './BettingPage';
import './App.css';
import { SPORTS_BETTING_CONTRACT_ADDRESS } from './config';
import SportsBettingABI from './SportsBettingABI.json';

function App() {
  const [isWalletConnected, setIsWalletConnected] = useState(false);

  const handleWalletConnection = (connected) => {
    setIsWalletConnected(connected);
  };

  return (
    <Router>
      <div className="App">
        <ConnectButton onWalletConnect={handleWalletConnection} />
        <header className="App-header">
          <h1>NBA Live Betting</h1>
        </header>
        <Routes>
          <Route path="/" element={<LiveScores isWalletConnected={isWalletConnected} />} />
          <Route
            path="/bet/:gameId"
            element={
              <BettingPage
                contractAddress={SPORTS_BETTING_CONTRACT_ADDRESS}
                abi={SportsBettingABI}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
